[{"fieldname": "print_heading_template", "fieldtype": "Custom HTML", "options": "{% set company_values = frappe.db.get_value("Company", doc.company, ["logo_for_printing"], as_dict=True) %}

{% set address = frappe.get_all("Dynamic Link", 
    filters={"parenttype": "Address", "link_doctype": "Company", "link_name": doc.company}, 
    pluck="parent") %}

{% if address %}
    {% set company_address = frappe.db.get_value("Address", address[0], ["address_line1", "city", "state", "pincode", "country","gstin"], as_dict=True) %}
{% else %}
    {% set company_address = None %}
{% endif %}

<table style="width: 100%; vertical-align: top; border: none;">
    <tbody>
        <tr>
            <!-- Left: Company Logo -->
            <td style="width: 25%; vertical-align: middle; text-align: left; padding: 10px; border: none;">
                {% if company_values.logo_for_printing %}
                    <img src="{{ company_values.logo_for_printing }}" style="height: 80px; object-fit: contain;">
                {% endif %}
            </td>

            <!-- Center: Company Name & Address -->
            <td style="width: 50%; vertical-align: middle; text-align: center; border: none;">
                <strong>{{ doc.company }}</strong><br>
                <small>
                    {% if company_address %}
                        {{ company_address.address_line1 or '' }}, {{ company_address.city or '' }},
                        {{ company_address.state or '' }} - {{ company_address.pincode or '' }},
                        {{ company_address.country or '' }},
                        GSTIN:{{ company_address.gstin or '' }}
                    {% else %}
                        No Address Found
                    {% endif %}
                </small>  <!-- Fetching linked address -->
            </td>
            <!-- Right: Document Title (Purchase Invoice / Debit Note) -->
            <td style="width: 25%; vertical-align: middle; text-align: right; padding: 10px; border: none;">
                <h4 style="margin: 0;">
                    {% if doc.is_return %}
                        Credit Note
                    {% else %}
                        Sales Invoice
                    {% endif %}
                </h4>
                <small class="sub-heading">{{ doc.name }}</small>
            </td>
        </tr>
    </tbody>
</table>
